def getStopwordsList(language):
    if language == 'fr-FR':
        # fr_stopwords.remove("personne")

        for word in customStopWordsFr():
            fr_stopwords.add(word)
        for word in customStopWordsEn():
            fr_stopwords.add(word)
        for word in en_stopwords:
            fr_stopwords.add(word)

        return fr_stopwords

    elif language == 'en-US':
        for word in customStopWordsEn():
            en_stopwords.add(word)
        for word in en_stopwords:
            en_stopwords.add(word)

        return en_stopwords

    elif language == 'es-ES':
        for word in customStopWordsEs():
            es_stopwords.add(word)
        for word in es_stopwords:
            es_stopwords.add(word)
        for word in en_stopwords:
            es_stopwords.add(word)
        return es_stopwords